import React, { Component } from 'react';
import FlipClock from 'flipclock';

class Countdown extends Component {
    constructor(props) {
        super(props);
        this.myRef = React.createRef();
    }

    componentDidMount() {
        const el = this.myRef.current;
        const currentDate = new Date();
        const deployDate = new Date('2019-05-31');
        const diff = (deployDate.getTime() / 1000) - (currentDate.getTime() / 1000);
        const clock = new FlipClock(el, diff, {clockFace: 'DailyCounter', countdown: true});

    }
    render() {
        return (
            <div className="my-clock" ref={this.myRef}></div>

        )

    }
}

export default Countdown;
