import React, { Component } from 'react';
import FlipClock from 'flipclock';

class Countdown extends Component {
    render() {
        const el = this.refs.clock;
        const clock = new FlipClock(el, new Date(), {face: 'HourCounter'});
        return (
            <div id="clock"></div>
        )
    }
}

export default Countdown;
