import React, { Component } from 'react';
import FlipClock from 'flipclock';
import 'jquery/dist/jquery.js';

class Countdown extends Component {
    constructor(props) {
        super(props);
        this.myRef = React.createRef();
    }

    componentDidMount() {
        const el = this.myRef.current;
        const currentDate = new Date();
        const pastDate = new Date(currentDate.getFullYear(), 0, 1);
        const diff = currentDate.getTime() / 1000 - pastDate.getTime() / 1000;
        const clock = new FlipClock(el, diff, {clockFace: 'DailyCounter', countdown: true})


    }
    render() {

        return (
            <div className="clock" ref={this.myRef}></div>

        )

    }
}

export default Countdown;
