import React, { Component } from "react";
import PropTypes from 'prop-types';

class ComingSoon extends Component {
  state = {
    value: ""
  };

  change = e => {
    this.setState({ value: e.target.value });
  };

  submit = e => {
    e.preventDefault();

    if (this.state.value) {
      this.props.subscribe.submit(this.setState.value);
      this.setState({ value: "" });
    }
  };

  render() {
    const {
      background,
      title,
      description,
      logo,
      subscribe,
      links
    } = this.props;

    return (
      <div
        className="background"
        style={{
          backgroundImage: `url(${background.image})`,
          backgroundColor: background.color,
          color: background.textColor,
          ...background.style
        }}
      >
        <div className="logo-container">
          <img
            className="logo"
            alt={logo.alt}
            src={logo.src}
            style={{ ...logo.style }}
          />
        </div>
        <div className="title" style={{ ...title.style }}>
          {title.text}
        </div>
        <div className="description" style={{ ...description.style }}>
          {description.text}
        </div>
        <div className="subscribe">
          <form onSubmit={this.submit}>
            <input
              className="subscribe-email"
              type="email"
              placeholder={subscribe.placeholder}
              onChange={this.change}
              style={{ ...subscribe.inputStyle }}
              value={this.state.value}
            />
            <button
              className="subscribe-button"
              type="submit"
              style={{ ...subscribe.buttonStyle }}
            >
              {subscribe.buttonText}
            </button>
          </form>
        </div>
        <div className="social-links">
          {links.map((link, i) => {
            return (
              <a
                className="social-link"
                key={i}
                target="_blank"
                rel="noopener noreferrer"
                href={link.url}
              >
                <img
                  className="social-logo"
                  src={link.logo}
                  alt={link.text}
                  style={{ ...link.logoStyle }}
                />
                <span className="social-text" style={{ ...link.textStyle }}>
                  {link.text}
                </span>
              </a>
            );
          })}
        </div>
      </div>
    );
  }
}

ComingSoon.defaultProps = {
  background: {
    image: '',
    color: '',
    textColor: '',
    style: {}
  },
  logo: {
    alt: '',
    src: '',
    style: {}
  },
  title: {
    text: '',
    style: {}
  },
  description: {
    text: '',
    style: {}
  },
  subscribe: {
    placeholder: '',
    inputStyle: {},
    buttonText: '',
    buttonStyle: {}
  },
  links: [{
    url: '',
    logo: '',
    text: '',
    logoStyle: {},
    textStyle: {}
  }]
};

export default ComingSoon;
