import React, { Component } from "react";
import "../css/Subscribe.css";

class Subscribe extends Component {
  constructor(props) {
    super(props)

    this.state = {
      email: ""
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  handleChange(e) {
    this.setState({ email: e.target.value });
  };

  handleSubmit(e) {
    e.preventDefault();

    if (!('fetch' in window)) {
      console.log('Fetch not found');
      return
    }

    if (this.state.email) {
      const authString = btoa(`anystring:a39c379dd943a525d5c7cb866390e277-us20`);
      console.log(process.env.API_KEY);
      console.log(process.env.LIST_ID);
      fetch(`https://us20.api.mailchimp.com/3.0/lists/fb3a49a977/members/`, {
        method: 'POST',
        //mode: 'no-cors',
        //cache: 'no-cache',
        //credentials: "same-origin",
        headers: {
          'Content-Type': 'application/json',
          'Authorization': 'Basic ' + authString
        },
        //redirect: 'follow',
        //referrer: 'no-referrer',
        body: JSON.stringify({
          email_address: this.state.email,
          status: 'subscribed'
        }),
      }).then(data => console.log(data)).catch(error => console.log(`Error: ${error}`));

      this.setState({ email: "" });
    }
  };

  render() {
    const { placeholder, buttonText } = this.props;

    return (
      <form className="subscribe" onSubmit={this.handleSubmit}>
        <input
          className="subscribe-email"
          name="email"
          type="email"
          placeholder={placeholder}
          onChange={this.handleChange}
          value={this.state.email}
        />
        <button className="subscribe-button" type="submit">
          {buttonText}
        </button>
      </form>
    );
  }
}

export default Subscribe;
