import React, { Component } from "react";
import Moment from "react-moment";

class Countdown extends Component {
  state = {
    days: 0,
    hours: 0,
    minutes: 0,
    seconds: 0
  };

  componentDidMount() {
    const deployDate = new Date(this.props.deployDate);
    setInterval(() => {
      let remainder;
      let today = new Date();
      let dateDiff = deployDate.getTime() / 1000 - today.getTime() / 1000;
      let days = Math.floor(dateDiff / 86400);
      remainder = dateDiff % 86400;
      let hours = Math.floor(remainder / 3600);
      remainder = remainder % 3600;
      let minutes = Math.floor(remainder / 60);
      let seconds = Math.floor(remainder % 60);
      this.setState({
        days,
        hours,
        minutes,
        seconds,
        deployDate
      });
    }, 1000);
  }
  render() {
    return (
      <div className="countdown-clock">
        {Object.values(this.state).filter(value => value !== 0).length === 0 ? (
          ''
        ) : (
          <div>{`Days: ${this.state.days} Hours: ${this.state.hours} Minutes: ${
            this.state.minutes
          } Seconds: ${this.state.seconds}`}</div>
        )}
      </div>
    );
  }
}

export default Countdown;
