import React from 'react';

export const Background = ({
    image,
    color,
    textColor,
    style
}) => {
    return (
        <div
        className="background"
        style={{
          backgroundImage: `url(${image})`,
          backgroundColor: color,
          color: textColor,
          ...style
        }}
      ></div>
    )
}
