import React, { Component } from "react";
import moment, { duration } from 'moment'

class Countdown extends Component {
  state = {
    days: 0,
    hours: 0,
    minutes: 0,
    seconds: 0
  };

  componentDidMount() {
    const deployDate = moment(this.props.deployDate);
    setInterval(() => {

      let today = moment();
      let days = deployDate.diff(today, 'days');
      let hours = deployDate.diff(today - days, 'hours');
      let minutes = deployDate.diff(today, 'minutes');
      let seconds = deployDate.diff(today, 'seconds');
      //let days = duration(dateDiff, 'd').days();
      //let dateDiff = deployDate.getTime() / 1000 - today.getTime() / 1000;
      //let days = Math.floor(dateDiff / 86400);
    //   remainder = dateDiff % 86400;
    //   let hours = Math.floor(remainder / 3600);
    //   remainder = remainder % 3600;
    //   let minutes = Math.floor(remainder / 60);
    //   let seconds = Math.floor(remainder % 60);
      this.setState({
        days,
        hours,
        minutes,
        seconds
      });
    }, 1000);
  }
  render() {
    return (
      <div className="countdown-clock">
        {Object.values(this.state).filter(value => value !== 0).length === 0 ? (
          <div className="loading-countdown">{this.props.loadingMessage}</div>
        ) : (
          Object.keys(this.state).map((key, i) => (
            <div key={i} className="countdown-clock-segment">
              <span className="segment">{this.state[key]}</span>
              <span className="segment-title">{key.toUpperCase()}</span>
            </div>
          ))
        )}
      </div>
    );
  }
}

export default Countdown;
