import React, { Component } from 'react';
import Moment from 'react-moment';

class Countdown extends Component {
    componentDidMount() {
        let diff;
        let deployDate = '2019-05-31 23:59:59';
        setInterval(() => {
            let remainder;
            let today = Date.now();
            diff = (today.getTime() / 1000) - (deployDate.getTime() / 1000)
            this.props.days = Math.floor(diff / 86400)
            remainder = (diff % 86400)
            this.props.hours = Math.floor(remainder / 3600)
            remainder = (remainder % 3600)
            this.props.minutes = Math.floor(remainder / 60)
            this.props.seconds = (remainder % 60)
        }, 1000)

    }
    render() {

        return (
            <div className="countdown-clock">
                <div>{`Days: ${this.props.days} Hours: ${this.props.hours} Minutes: ${this.props.minutes} Seconds: ${this.props.seconds}`}</div>

            </div>

        )

    }
}

export default Countdown;
