import React, { Component } from 'react';
import Moment from 'react-moment';

class Countdown extends Component {
    state = {
        days: 0,
        hours: 0,
        minutes: 0,
        seconds: 0,
        deployDate: ''
    }
    componentDidMount() {
        let diff;
        let deployDate = new Date('2019-05-31 23:59:59');
        setInterval(() => {
            let remainder;
            let today = new Date();
            diff = (deployDate.getTime() / 1000) - (today.getTime() / 1000)
            let days = Math.floor(diff / 86400)
            remainder = (diff % 86400)
            let hours = Math.floor(remainder / 3600)
            remainder = (remainder % 3600)
            let minutes = Math.floor(remainder / 60)
            let seconds = Math.floor(remainder % 60)
            this.setState({
                days,
                hours,
                minutes,
                seconds,
                deployDate
            })
        }, 1000)

    }
    render() {

        return (
            <div className="countdown-clock">

                <div>{`Days: ${this.state.days} Hours: ${this.state.hours} Minutes: ${this.state.minutes} Seconds: ${this.state.seconds}`}</div>
                <Moment fromNow>{this.state.deployDate.getTime()}</Moment>
            </div>

        )

    }
}

export default Countdown;
