import React, { Component } from 'react';
import FlipClock from 'flipclock';

class Countdown extends Component {
    constructor(props) {
        super(props);
        this.myRef = React.createRef();
    }

    componentDidMount() {
        const el = this.myRef.current;
        const today = new Date('05-31-2019');
        const clock = new FlipClock(el, today, {clockFace: 'DailyCounter', countdown: true});
        clock.start();
    }
    render() {

        return (
            <div className="clock" ref={this.myRef}></div>

        )

    }
}

export default Countdown;
