import React, { Component } from "react";
import moment, { duration } from "moment";
import PropTypes from 'prop-types';

class Countdown extends Component {
  state = {
    days: 0,
    hours: 0,
    minutes: 0,
    seconds: 0
  };

  componentDidMount() {
    const deployDate = moment(this.props.deployDate);
    this.interval = setInterval(() => {
      const today = moment();

      const clockDuration = duration(deployDate.diff(today));

      const days = clockDuration.days();
      const hours = clockDuration.hours();
      const minutes = clockDuration.minutes();
      const seconds = clockDuration.seconds();

      this.setState({
        days,
        hours,
        minutes,
        seconds
      });
    }, 1000);
  }

  render() {
    return (
      <div className="countdown-clock">
        {Object.values(this.state).filter(value => value !== 0).length === 0 ? (
          <div className="loading-countdown">{this.props.loadingMessage}</div>
        ) : (
          Object.keys(this.state).map((key, i) => (
            <div key={i} className="countdown-clock-segment">
              <span className="segment">{this.state[key]}</span>
              <span className="segment-title">{key.toUpperCase()}</span>
            </div>
          ))
        )}
      </div>
    );
  }
}

Countdown.propTypes = {
    date: PropTypes.string
}

export default Countdown;
